446.sql:
WITH abyssal_horror_set AS (
    SELECT code FROM sets WHERE name = 'Abyssal Horror'
),
total_cards AS (
    SELECT COUNT(*) AS total FROM cards WHERE setCode = (SELECT code FROM abyssal_horror_set)
),
cmc_10_cards AS (
    SELECT COUNT(*) AS cmc_10 FROM cards WHERE setCode = (SELECT code FROM abyssal_horror_set) AND convertedManaCost = 10
)
SELECT (cmc_10 * 100.0 / NULLIF(total, 0)) AS percentage
FROM total_cards, cmc_10_cards;

